INSERT INTO `syjj_report` VALUES ('114', '', null, '', null, '', '', null, null, '罗租公园', '罗租公园放音乐', null, null, '罗租公园', null, '其他', '罗租公园跳广场舞收费', '违规从事营利活动', '罗租公园本是公共放松场所，属于政府管理，为何放跳广场舞音乐的那个人每天都强行收费。谢谢帮忙处理。', '/Uploads/2016-09-25/1474773844.zip', '0', null, '1474773844', '17121418', '2', '0', null, '你好，廉洁石岩主要是处理党员干部违纪违法投诉问题，你所投诉的不属于党员干部违纪违法问题，我们将你的投诉转相关业务部门处理。', null);
INSERT INTO `syjj_report` VALUES ('115', '', null, '', null, '', '', null, null, '深圳石岩罗租公园', '深圳石岩罗租公园', null, null, '深圳石岩罗租公园', null, '其他', '深圳宝安石岩罗租公园乱收费', '违规从事营利活动', '下班后难得有个公园放松的地方，可公园被广场舞占了，心想，这样也好，我们也活动下，可放广场舞的那个人每次都要强行收费，这是国家的地方，你私自收费合理吗', '/Uploads/2016-10-04/1475591053.zip', '0', null, '1475591053', '28830814', '2', '0', null, '你好，廉洁石岩公众号主要是处理党员干部违纪违法问题，您所投诉的问题不属于党员干部违纪违法。', null);
